mapper.insert:
INSERT IGNORE INTO movie(apiId, title, descr, year, imgLink, genre)
VALUES 

	(
	#{item.apiId}, #{item.title}, #{item.descr}, #{item.year},#{item.imgLink}, #{item.genre}
	)

mapper.select:
select apiId, title, descr, year, imgLink, genre
from movie
where genre like CONCAT('%',#{genre},'%');
